def HttpError(iErrorCode):    dCode = {        "200" : "OK",        "400" : "REQUEST BODY WRONG",        "401" : "PERMITION WRONG",        "403" : "PERMITION DENY",        "404" : "PAGE NO FOUND",        "405" : "SIGNATURE WRONG",        "406" : "VARABLE FORMAT MISTAKE",        "413" : "BODY TOO LARGE",        "417" : "SERVER EXECUTE FALSE",        "500" : "SERVER ERROR",        "501" : "HASH ERROR",        "502" : "EXECUTE OUT OF TIME",        "504" : "ROUTE OUT OF TIME",        "505" : "HTTP VERSION NOT SUPPORT",        "506" : "SERVER ON MAINTAINING",        "599" : "UNKONW ERROR"    }        if str(iErrorCode) not in dCode:        iErrorCode = 599    sMessage = dCode[str(iErrorCode)]    return {'E': iErrorCode, 'M': sMessage}     