import hashlibdef get(string):    string = str(string)    sMd5  = hashlib.new("md5", string).hexdigest()    return sMd5[-4:].upper()def check(string, sign):    if get(string) == sign.upper():        return True    return Falsedef hash(string, hashSet=8):    string = str(string)    sMd5  = hashlib.new("md5", string).hexdigest()    return int(sMd5[-4:], 16) % hashSet 