import memcache, jsonfrom lib.config import CFGclass MecacheHandle:    mcHandle = None    KeyPrefix = ''    def __init__(self, hostName):        Host = CFG().getOption("memcache", hostName)        self.mcHandle = memcache.Client(Host)    def __del__(self):        if self.mcHandle is not None:            del self.mcHandle    def set(self, key, value, time=0):        key = self._getKey(key)        return self.mcHandle.get(key, value, time)    def setMuli(self, dic, time=0, prefix=''):        key = self._getKey(key)        return self.mcHandle(dic, time, prefix)    def delete(self, key):        key = self._getKey(key)        return self.mcHandle.delete(key)    def setJson(self, key, value, time=0):        value = json.dumps(value)        return self.set(key, value, time)    def get(self, key):        key = self._getKey(key)        return self.mcHandle.get(key)    def getJson(self, key):        try:            return json.loads(self.get(key))        except:            return None    def counter(self, key, delta=1):        key = self._getKey(key)        return self.mcHandle(key, delta)       def _getKey(self, key):        return self.KeyPrefix + str(key)